mapper.sql:
/* 고객을 등록 / 고객과 연계된 계약의 멤버들 */
AND (  B.BUYER_ID IN ( SELECT DISTINCT C.BUYER_ID  
						FROM TBL_EXP_CONTRACT C
						,TBL_EXP_CONTRACT_MEMBER CM 
						WHERE C.CONTRACT_ID = CM.CONTRACT_ID 
						AND C.USE_YN = 'Y' AND C.DEL_YN = 'N'
						AND CM.RELATION_TYPE = 'EXECUTE'
						AND ( C.USER_ID = #{sessionVo.userId}
						  OR CM.USER_ID = #{sessionVo.userId}
						)
		)
)

mapper.delete:
/*	cstm.deleteCstm
	고객  삭제
*/
UPDATE TBL_EXP_BUYER
SET USE_YN = 'N' 
, DEL_YN = 'Y' 
,UPDATE_USER = #{sessionVo.userId}
,UPDATE_DATE = SYSDATE
WHERE BUYER_ID = #{BUYER_ID}

mapper.select:

    { CALL
	    DECLARE
	        p_BUYER_ID		VARCHAR2(30);
			p_OLD_BUYER_ID	VARCHAR2(30);
	    BEGIN

	        p_BUYER_ID 		:= #{BUYER_ID};
	        p_OLD_BUYER_ID 	:= #{OLD_BUYER_ID};

	        UPDATE TBL_EXP_BUYER
	        SET BUYER_ID 		= p_BUYER_ID 
			,	UPDATE_USER 	= #{sessionVo.userId}
			,	UPDATE_DATE 	= SYSDATE
	        WHERE BUYER_ID		= p_OLD_BUYER_ID
	        ;

	        UPDATE TBL_EXP_CONTRACT
	        SET BUYER_ID 		= p_BUYER_ID
	        WHERE BUYER_ID 		= p_OLD_BUYER_ID
	        ;

	    END
    }

/* cstm.getBuyerSellingList 매출 top RANK 거래처 */ SELECT * FROM ( SELECT CONTRACT_YEAR,CONTRACT_MONTH ,BUYER_ID, BUYER_NM, HOW_GET, HOW_GET_NM ,CONTRACT_TYPE, CONTRACT_TYPE_NM ,COMPANY_REGNUM ,CONSULTING_NM ,SUM( TOTAL_COST ) TOTAL_COST ,SUM( SUM_INCOME ) TOT_INCOME FROM(
SELECT ROWNUM , TO_CHAR( CONTRACT_DATE , 'YYYY' ) AS CONTRACT_YEAR , TO_CHAR( CONTRACT_DATE , 'MM' ) AS CONTRACT_MONTH , BUYER_ID , BUYER_NM , HOW_GET , GET_CODE_NM( 'HOW_GET', HOW_GET ) HOW_GET_NM , CONTRACT_TYPE , GET_CODE_NM('CONTRACT_TYPE', CONTRACT_TYPE ) CONTRACT_TYPE_NM , COMPANY_REGNUM , CONSULTING_NM , TOTAL_COST , SUM_INCOME FROM ( SELECT B.BUYER_NM,B.HOW_GET,B.COMPANY_REGNUM, ( SELECT SUM(R.INCOME) FROM TBL_EXP_RESULT R WHERE R.CONTRACT_ID = C.CONTRACT_ID AND R.USE_YN = 'Y' AND R.DEL_YN = 'N' ) SUM_INCOME ,CS.CONSULTING_NM ,C.TOTAL_COST ,C.CONTRACT_DATE ,C.BUYER_ID ,C.CONTRACT_TYPE FROM TBL_EXP_CONTRACT C, TBL_EXP_BUYER B, TBL_EXP_CONSULTING CS ,TBL_EXP_CONTRACT_MEMBER CM WHERE 1 = 1 AND C.BUYER_ID = B.BUYER_ID
AND C.CONTRACT_DATE >= TO_DATE( #{parm.FROM_CONTRACT_DATE} , 'YYYY-MM-DD')
AND C.CONTRACT_DATE <= TO_DATE( #{parm.TO_CONTRACT_DATE} , 'YYYY-MM-DD')
AND C.PROCESS_STATE !='PRE_CONTRACT' AND B.USE_YN = 'Y' AND B.DEL_YN = 'N' AND C.USE_YN = 'Y' AND C.DEL_YN = 'N' AND C.CONSULTING_ID = CS.CONSULTING_ID
AND CS.CONSULTING_TYPE = #{parm.CONSULTING_TYPE}
AND C.PROCESS_STATE != 'PRE_CONTRACT' AND C.CONTRACT_ID = CM.CONTRACT_ID AND CM.RELATION_TYPE = 'EXECUTE' AND ( C.USER_ID = #{sessionVo.userId } OR CM.USER_ID = #{sessionVo.userId } ) ORDER BY SUM_INCOME DESC ) ) GROUP BY CONTRACT_YEAR,CONTRACT_MONTH ,BUYER_ID, BUYER_NM, HOW_GET, HOW_GET_NM ,CONTRACT_TYPE, CONTRACT_TYPE_NM ,COMPANY_REGNUM ,CONSULTING_NM )
WHERE ROWNUM <= #{parm.RANK}
