mapper.insert:
 /* Filemng.saveFileInfo */
INSERT INTO TBL_EXP_FILEMNG(
      GRP_FILE_ID
     ,FILE_ID
     ,ORG_FILE_NM
     ,STORED_FILE_NM
     ,FILE_PATH
     ,FILE_SIZE
     ,FILE_TYPE
     ,OWNER_CD
     ,ORDER_NUM
     ,DEL_YN
     ,SP_CSTM_ID
     ,CREATE_USER
     ,CREATE_DATE
 )
 VALUES(
      #{GRP_FILE_ID}
     ,#{FILE_ID}
     ,#{ORG_FILE_NM}
     ,#{STORED_FILE_NM}
     ,#{FILE_PATH} 
     ,TO_NUMBER(#{FILE_SIZE})
     ,#{FILE_TYPE}
     ,#{OWNER_CD}
     ,(SELECT NVL2( MAX(ORDER_NUM), MAX(ORDER_NUM)+1 ,1 ) AS ORDER_NUM FROM TBL_EXP_FILEMNG WHERE GRP_FILE_ID = #{GRP_FILE_ID})
     ,'N'
     ,#{sessionVo.spCstmId}
     ,#{sessionVo.userId}
     ,sysdate
 )

mapper.update:
/*	Filemng.getFileList
	업로드 File 정보 조회
*/
UPDATE TBL_EXP_FILEMNG SET
	DEL_YN = 'Y'
WHERE 1=1
	AND GRP_FILE_ID = #{GRP_FILE_ID}
	AND OWNER_CD 	= #{OWNER_CD}
	AND DEL_YN 		= 'N'
