mapper.update:
/* hrInfo.updateMyHrInfo 내 정보 보기 수정 */ UPDATE TBL_EXP_USER T SET T.USER_NM = #{USER_NM}, T.GENDER = #{GENDER}, T.USER_DESC = #{USER_DESC}, T.E_MAIL = #{E_MAIL}, T.PHONE_NUM = #{PHONE_NUM}, T.JUMIN = #{JUMIN}, T.POST_NUM = #{POST_NUM}, T.ADDR1 = #{ADDR1}, T.ADDR2 = #{ADDR2},
T.PIC_NUM = #{PIC_NUM},
T.PIC_NUM = '1' ,
T.RECOMMENDER_ID = #{RECOMMENDER_ID},
T.RECOMMENDER_ID = NULL,
T.UPDATE_USER = #{sessionVo.userId }, T.UPDATE_DATE = SYSDATE WHERE T.SP_CSTM_ID = #{sessionVo.spCstmId } AND T.USER_ID = #{sessionVo.userId }

mapper.delete:
	/*	hrInfo.deleteHrInfo
		인사정보  삭제
	*/
    { CALL
	    DECLARE
	        p_USER_ID		VARCHAR2(30);
	    BEGIN

	        p_USER_ID	:= #{USER_ID};

			UPDATE TBL_EXP_USER
			SET STATE = 'DELETED' 
			,USE_YN = 'N'
			,DEL_YN = 'Y'
			,UPDATE_USER = #{sessionVo.userId}
			,UPDATE_DATE = SYSDATE
			WHERE SP_CSTM_ID = #{sessionVo.spCstmId}
			AND	  USER_ID	 = #{USER_ID}
	        ;

	        DELETE FROM TBL_EXP_CAREER_HIST
			WHERE SP_CSTM_ID = #{sessionVo.spCstmId}
			AND	  USER_ID	 = #{USER_ID}
	        ;

	        DELETE FROM TBL_EXP_EDUCATION_HIST
			WHERE SP_CSTM_ID = #{sessionVo.spCstmId}
			AND	  USER_ID	 = #{USER_ID}
	        ;

	        DELETE FROM TBL_EXP_GRADE_HIST
			WHERE SP_CSTM_ID = #{sessionVo.spCstmId}
			AND	  USER_ID	 = #{USER_ID}
	        ;

	        DELETE FROM TBL_EXP_POSITION_HIST
			WHERE SP_CSTM_ID = #{sessionVo.spCstmId}
			AND	  USER_ID	 = #{USER_ID}
	        ;

	        COMMIT;
	    END
    }

mapper.update:
/*	hrInfo.updateFileImg
	인사정보 직원 이미지 File 정보 수정
*/
UPDATE TBL_EXP_FILEMNG SET
	GRP_FILE_ID = #{GRP_FILE_ID} 
WHERE 1=1
	AND GRP_FILE_ID = #{OLD_GRP_FILE_ID}
	AND OWNER_CD 	= 'USER_FACE'
	AND DEL_YN 		= 'N'

/* hrInfo.updateSuretyInsurance 보증보험 저장 */ MERGE INTO TBL_EXP_SURETY_INSURANCE T USING (
SELECT #{sessionVo.spCstmId} AS SP_CSTM_ID, #{USER_ID} AS USER_ID, TO_NUMBER( REPLACE(#{AMOUNT},',','') ) AS AMOUNT, TO_DATE( #{INSURANCE_REG_DATE} , 'YYYY-MM-DD' ) AS REG_DATE, TO_DATE( #{INSURANCE_EXP_DATE} , 'YYYY-MM-DD' ) AS EXP_DATE FROM dual
) S ON (T.SP_CSTM_ID = S.SP_CSTM_ID AND T.USER_ID = S.USER_ID ) WHEN MATCHED THEN UPDATE SET T.AMOUNT = S.AMOUNT, T.REG_DATE = S.REG_DATE, T.EXP_DATE = S.EXP_DATE WHEN NOT MATCHED THEN INSERT ( SP_CSTM_ID, USER_ID, AMOUNT, REG_DATE, EXP_DATE) VALUES (S.SP_CSTM_ID, S.USER_ID, S.AMOUNT, S.REG_DATE, S.EXP_DATE)

/* hrInfo.updateEducationHist 학력 리스트 저장 */ MERGE INTO TBL_EXP_EDUCATION_HIST T USING (
SELECT #{sessionVo.spCstmId} AS SP_CSTM_ID, #{USER_ID} AS USER_ID, #{GUBUN} AS GUBUN, TO_DATE(#{START_PERIOD_DATE},'YYYY-MM-DD') AS START_PERIOD_DATE, TO_DATE(#{END_PERIOD_DATE},'YYYY-MM-DD') AS END_PERIOD_DATE, #{SCHOOL_NAME} AS SCHOOL_NAME, #{MAJOR} AS MAJOR, #{GRADUATE_YN} AS GRADUATE_YN, #{EDUCATION_ID} AS EDUCATION_ID FROM dual
) S ON (T.SP_CSTM_ID = S.SP_CSTM_ID AND T.USER_ID = S.USER_ID AND T.EDUCATION_ID = S.EDUCATION_ID ) WHEN MATCHED THEN UPDATE SET T.GUBUN = S.GUBUN, T.SCHOOL_NAME = S.SCHOOL_NAME, T.MAJOR = S.MAJOR, T.GRADUATE_YN = S.GRADUATE_YN, T.START_PERIOD_DATE = S.START_PERIOD_DATE, T.END_PERIOD_DATE = S.END_PERIOD_DATE WHEN NOT MATCHED THEN INSERT ( SP_CSTM_ID, USER_ID, GUBUN, START_PERIOD_DATE, END_PERIOD_DATE, SCHOOL_NAME, MAJOR, GRADUATE_YN, EDUCATION_ID) VALUES (S.SP_CSTM_ID, S.USER_ID, S.GUBUN, S.START_PERIOD_DATE, S.END_PERIOD_DATE, S.SCHOOL_NAME, S.MAJOR, S.GRADUATE_YN,S.EDUCATION_ID)

mapper.delete:
	/*	hrInfo.deleteEducationHist
		학력 리스트 삭제 
	*/
 	DELETE FROM TBL_EXP_EDUCATION_HIST T
 	WHERE T.SP_CSTM_ID		= #{sessionVo.spCstmId}
	AND T.USER_ID 			= #{USER_ID}
	AND T.EDUCATION_ID 		= #{EDUCATION_ID}

mapper.update:
/* hrInfo.updateCareerHist 경력 리스트 저장 */ MERGE INTO TBL_EXP_CAREER_HIST T USING (
SELECT #{sessionVo.spCstmId} AS SP_CSTM_ID, #{USER_ID} AS USER_ID, #{GUBUN} AS GUBUN, TO_DATE(#{START_PERIOD_DATE},'YYYY-MM-DD') AS START_PERIOD_DATE, TO_DATE(#{END_PERIOD_DATE},'YYYY-MM-DD') AS END_PERIOD_DATE, #{DEPARTMENT} AS DEPARTMENT, #{POSITION} AS POSITION, #{BIZ_INFORM} AS BIZ_INFORM, #{CAREER_ID} AS CAREER_ID FROM dual
) S ON (T.SP_CSTM_ID = S.SP_CSTM_ID AND T.USER_ID = S.USER_ID AND T.CAREER_ID = S.CAREER_ID ) WHEN MATCHED THEN UPDATE SET T.GUBUN = S.GUBUN, T.DEPARTMENT = S.DEPARTMENT, T.POSITION = S.POSITION, T.BIZ_INFORM = S.BIZ_INFORM, T.START_PERIOD_DATE = S.START_PERIOD_DATE, T.END_PERIOD_DATE = S.END_PERIOD_DATE
WHEN NOT MATCHED THEN INSERT ( SP_CSTM_ID, USER_ID, GUBUN, START_PERIOD_DATE, END_PERIOD_DATE, DEPARTMENT, POSITION, BIZ_INFORM, CAREER_ID) VALUES (S.SP_CSTM_ID, S.USER_ID, S.GUBUN, S.START_PERIOD_DATE, S.END_PERIOD_DATE, S.DEPARTMENT, S.POSITION, S.BIZ_INFORM,S.CAREER_ID)

mapper.delete:
	/*	hrInfo.deleteCareerHist
		경력 리스트 삭제 
	*/
 	DELETE FROM TBL_EXP_CAREER_HIST T
 	WHERE T.SP_CSTM_ID		= #{sessionVo.spCstmId}
	AND T.USER_ID 			= #{USER_ID}
	AND T.CAREER_ID 		= #{CAREER_ID}

mapper.update:
/* hrInfo.updatePositionHist 직책 승급 리스트 저장 ( hr 역할 ) */ MERGE INTO TBL_EXP_POSITION_HIST T USING (
SELECT #{sessionVo.spCstmId} AS SP_CSTM_ID, #{USER_ID} AS USER_ID, #{DEPARTMENT} AS DEPARTMENT, #{POSITION} AS POSITION, TO_DATE(#{PERSONAL_APPOINTMENT_DATE},'YYYY-MM-DD') AS PERSONAL_APPOINTMENT_DATE, #{POSITION_ID} AS POSITION_ID FROM dual
) S ON (T.SP_CSTM_ID = S.SP_CSTM_ID AND T.USER_ID = S.USER_ID AND T.POSITION_ID = S.POSITION_ID ) WHEN MATCHED THEN UPDATE SET T.DEPARTMENT = S.DEPARTMENT , T.POSITION = S.POSITION , T.PERSONAL_APPOINTMENT_DATE = S.PERSONAL_APPOINTMENT_DATE WHEN NOT MATCHED THEN INSERT ( SP_CSTM_ID, USER_ID, DEPARTMENT, POSITION, PERSONAL_APPOINTMENT_DATE, POSITION_ID) VALUES ( S.SP_CSTM_ID, S.USER_ID, S.DEPARTMENT, S.POSITION, S.PERSONAL_APPOINTMENT_DATE,S.POSITION_ID)

mapper.delete:
	/*	hrInfo.deletePositionHist
		직책 승급 삭제 
	*/
 	DELETE FROM TBL_EXP_POSITION_HIST T
 	WHERE T.SP_CSTM_ID	= #{sessionVo.spCstmId}
	AND T.USER_ID		= #{USER_ID}
	AND T.POSITION_ID	= #{POSITION_ID}

mapper.update:
/* hrInfo.updateGradeHist 등급 승급 리스트 저장 ( hr 등급 ) */ MERGE INTO TBL_EXP_GRADE_HIST T USING (
SELECT #{sessionVo.spCstmId} AS SP_CSTM_ID, #{USER_ID} AS USER_ID, TO_DATE(#{UPGRADE_DATE},'YYYY-MM-DD') AS UPGRADE_DATE, #{GRADE} AS GRADE, #{GRADE_ID} AS GRADE_ID FROM dual
) S ON (T.SP_CSTM_ID = S.SP_CSTM_ID AND T.USER_ID = S.USER_ID AND T.GRADE_ID = S.GRADE_ID ) WHEN MATCHED THEN UPDATE SET T.GRADE = S.GRADE ,T.UPGRADE_DATE = S.UPGRADE_DATE WHEN NOT MATCHED THEN INSERT ( SP_CSTM_ID, USER_ID, UPGRADE_DATE, GRADE, GRADE_ID) VALUES ( S.SP_CSTM_ID, S.USER_ID, S.UPGRADE_DATE, S.GRADE, S.GRADE_ID)

mapper.delete:
	/*	hrInfo.deleteGradeHist
		등급 승급 삭제 
	*/
 	DELETE FROM TBL_EXP_GRADE_HIST T
 	WHERE T.SP_CSTM_ID	= #{sessionVo.spCstmId}
	AND T.USER_ID		= #{USER_ID}
	AND T.GRADE_ID		= #{GRADE_ID}
