mapper.select:
/* operation.getConsultingItem 컨설팅 조회 */ SELECT DISTINCT CT.CONSULTING_ID , CT.CONSULTING_NM , CT.CONSULTING_TYPE , CT.CONSULTING_DESC , CASE WHEN ( SELECT COUNT(1) FROM TBL_EXP_CONTRACT I WHERE I.CONSULTING_ID = CT.CONSULTING_ID AND I.USE_YN ='Y' AND DEL_YN='N') > 0 THEN 'N' ELSE 'Y' END AS CAN_DEL FROM TBL_EXP_CONSULTING CT , TBL_EXP_CONTRACT R WHERE 1=1
AND CT.CONSULTING_ID = #{CONSULTING_ID}
AND CT.CONSULTING_ID = R.CONSULTING_ID(+) ORDER BY CT.CONSULTING_NM

mapper.delete:
/*	operation.deleteConsultingItem
	컨설팅 삭제
*/
DELETE FROM  TBL_EXP_CONSULTING
WHERE CONSULTING_ID	= #{CONSULTING_ID}

mapper.update:
		/*	operation.saveWorkHistory
			WORK HISTORY 저장
		*/
        INSERT INTO TBL_EXP_WORK_HISTORY (
			 WH_ID       
			,USER_ID     
			,WORK_CONTENT
		    ,UPDATE_USER 
		    ,UPDATE_DATE 
		    ,SP_CSTM_ID  
        )
        VALUES(
		    #{WH_ID       		}   
		   ,#{USER_ID     		}   
		   ,#{WORK_CONTENT		}
           ,#{sessionVo.userId 	}
           ,sysdate
           ,#{sessionVo.spCstmId}
        )
