mapper.sql:
SELECT ROW_NUMBER() OVER( ORDER BY C.TOTAL_COST DESC ,TO_CHAR( C.CONTRACT_DATE , 'YYYY' ), TO_CHAR( C.CONTRACT_DATE , 'MM' )) RN , C.CONTRACT_ID , C.USER_ID , GET_USER_NM(C.USER_ID) USER_NM , U.SALES_TYPE , '' AS TEAM_NM , GET_CODE_NM('SALES_TYPE', U.SALES_TYPE ) SALES_TYPE_NM , C.CONTACT_DATE , TO_CHAR( C.CONTRACT_DATE , 'YYYY' ) AS CONTRACT_YEAR , TO_CHAR( C.CONTRACT_DATE , 'MM' ) AS CONTRACT_MONTH , C.CONTRACT_TYPE , GET_CODE_NM('CONTRACT_TYPE', C.CONTRACT_TYPE ) CONTRACT_TYPE_NM , C.EXPIRE_DATE , C.TOTAL_COST , C.CONSULTING_ID , C.PROCESS_STATE , C.LAST_SOLUTION , C.TAX_MNG , C.LABOR_MNG , C.CONSULTING_CLASS , C.USE_YN , C.DEL_YN , C.UPDATE_USER , C.UPDATE_DATE , B.BUYER_ID , B.BUYER_NM , B.COMPANY_REGNUM , B.HOW_GET , GET_CODE_NM( 'HOW_GET', B.HOW_GET ) HOW_GET_NM , CS.CONSULTING_TYPE , CS.CONSULTING_NM , NVL((SELECT SUM ( RE.INCOME ) FROM TBL_EXP_RESULT RE WHERE C.CONTRACT_ID = RE.CONTRACT_ID AND RE.USE_YN ='Y' AND RE.DEL_YN = 'N' ),0) AS TOT_INCOME ,RANK() OVER (ORDER BY (SELECT SUM(RE.INCOME) FROM TBL_EXP_RESULT RE WHERE C.CONTRACT_ID = RE.CONTRACT_ID AND RE.USE_YN ='Y' AND RE.DEL_YN = 'N' ) ) AS RANK FROM TBL_EXP_CONTRACT C ,TBL_EXP_BUYER B ,TBL_EXP_CONSULTING CS ,TBL_EXP_USER U ,TBL_EXP_CONTRACT_MEMBER CM WHERE 1=1 AND C.BUYER_ID = B.BUYER_ID AND C.USE_YN = 'Y' AND C.DEL_YN ='N' AND C.CONSULTING_ID = CS.CONSULTING_ID AND B.USE_YN = 'Y' AND B.DEL_YN ='N' AND C.USER_ID = U.USER_ID AND U.USE_YN = 'Y' AND U.DEL_YN = 'N' AND GET_CODE_NM( 'HOW_GET', B.HOW_GET ) IS NOT NULL
AND CS.CONSULTING_ID = #{parm.CONSULTING_ID}
AND CS.CONSULTING_TYPE = #{parm.CONSULTING_TYPE}
AND C.CONTRACT_DATE >= TO_DATE( #{parm.FROM_CONTRACT_DATE} , 'YYYY-MM-DD')
AND C.CONTRACT_DATE <= TO_DATE( #{parm.TO_CONTRACT_DATE} , 'YYYY-MM-DD')
AND C.PROCESS_STATE !='PRE_CONTRACT' AND C.CONTRACT_ID = CM.CONTRACT_ID AND CM.RELATION_TYPE = 'EXECUTE' AND ( C.USER_ID = #{sessionVo.userId } OR CM.USER_ID = #{sessionVo.userId } )

mapper.select:
/* rstMgn.getContractList 전체 계약 리스트 */
ORDER BY TOTAL_COST DESC ,CONTRACT_YEAR, CONTRACT_MONTH
FETCH NEXT #{parm.RANK} ROWS ONLY

/* rstMgn.getPaymentList 전체 수수료 지급 (월자문/1회성컨설팅) */
WITH MONTHS_24 AS () ,EACH_PAYMENT AS (
SELECT M.month, DATA.TOTAL_INCOME FROM MONTHS_24 M ,( SELECT RESULT_MONTH ,SUM( PAYMENT) TOTAL_INCOME FROM( SELECT DISTINCT TO_CHAR( R.RESULT_DATE, 'YYYYMM') AS RESULT_MONTH ,R.INCOME ,P.PAYMENT FROM TBL_EXP_RESULT R ,TBL_EXP_CONTRACT C ,TBL_EXP_PAYMENT P ,TBL_EXP_CONTRACT_MEMBER CM ,TBL_EXP_USER U WHERE C.CONTRACT_ID = R.CONTRACT_ID AND R.CONTRACT_ID = P.CONTRACT_ID AND R.RESULT_ID = P.RESULT_ID AND C.CONTRACT_TYPE = #{CONTRACT_TYPE} AND C.CONTRACT_ID = CM.CONTRACT_ID AND P.USER_ID = CM.USER_ID AND P.USER_ID = U.USER_ID AND R.USE_YN = 'Y' AND R.DEL_YN = 'N' AND P.USE_YN = 'Y' AND P.DEL_YN = 'N' AND CM.USE_YN = 'Y' AND CM.DEL_YN = 'N' AND TO_CHAR(R.RESULT_DATE, 'YYYY') >= TO_CHAR(ADD_MONTHS(TRUNC(SYSDATE), -12), 'YYYY') AND TO_CHAR(R.RESULT_DATE, 'YYYY') <= TO_CHAR(ADD_MONTHS(TRUNC(SYSDATE), 0), 'YYYY') ) GROUP BY RESULT_MONTH ) DATA WHERE M.MONTH = DATA.RESULT_MONTH(+) ) SELECT * FROM EACH_PAYMENT ORDER BY MONTH

/* rstMgn.getSellingGrowthRateList 순매출 성장률 */
WITH MONTHS_24 AS () ,RESULT_TAB_BY_MONTH AS (
SELECT R.EACH_MONTH , SUM(INCOME) AS TOT_INCOME , SUM(COST_PRICE) AS TOT_COST_PRICE , SUM(ORG_SELLING_PRICE) AS ORG_SELLING_PRICE FROM TBL_EXP_CONTRACT C ,TBL_EXP_CONTRACT_MEMBER CM ,() R WHERE C.CONTRACT_TYPE = 'MONTHLY' AND C.CONTRACT_ID = R.CONTRACT_ID AND ( C.PROCESS_STATE = 'CONTRACTED' OR C.PROCESS_STATE = 'ON_COMPLETED_APPR' OR C.PROCESS_STATE = 'COMPLETED' ) AND C.USE_YN = 'Y' AND C.DEL_YN = 'N' AND C.CONTRACT_ID = CM.CONTRACT_ID AND CM.RELATION_TYPE = 'EXECUTE' AND ( C.USER_ID = #{sessionVo.userId } OR CM.USER_ID = #{sessionVo.userId } ) GROUP BY R.EACH_MONTH ) ,RESULT_TAB AS (
/* 작년 1월 부터 올해 12월까지 월별 매출 및 실적*/ SELECT M.MONTH, SUBSTR(M.MONTH,1,4) YEAR , NVL(TOT_INCOME, 0) AS TOT_INCOME , NVL(TOT_COST_PRICE, 0) AS TOT_COST_PRICE , NVL(ORG_SELLING_PRICE, 0) AS ORG_SELLING_PRICE FROM MONTHS_24 M ,RESULT_TAB_BY_MONTH DATA WHERE M.MONTH = DATA.EACH_MONTH(+) ORDER BY M.MONTH
) ,SALES_GROWTH AS (
SELECT A.MONTH AS THIS_YEAR ,NVL(B.MONTH , TO_CHAR(TO_DATE(A.MONTH,'YYYYMM') -INTERVAL '1' YEAR,'YYYYMM') ) AS LAST_YEAR ,A.ORG_SELLING_PRICE AS THIS_YEAR_SALES ,B.ORG_SELLING_PRICE AS LAST_YEAR_SALES ,DECODE(B.ORG_SELLING_PRICE,0,0, (A.ORG_SELLING_PRICE / B.ORG_SELLING_PRICE - 1) * 100) AS GROWTH_RATE FROM RESULT_TAB A ,RESULT_TAB B WHERE A.YEAR = (B.YEAR (+) + 1 ) AND TO_NUMBER(SUBSTR(A.MONTH, 5, 2)) = TO_NUMBER(SUBSTR(B.MONTH(+), 5, 2)) )
SELECT THIS_YEAR, MAX(LAST_YEAR) LAST_YEAR ,ROUND(NVL(MAX( THIS_YEAR_SALES)/100000000 ,0),2) THIS_YEAR_SALES ,ROUND(NVL(MAX( LAST_YEAR_SALES)/100000000 ,0),2) LAST_YEAR_SALES ,ROUND(NVL(MAX( GROWTH_RATE) ,0),2) GROWTH_RATE FROM( SELECT G.THIS_YEAR, G.LAST_YEAR, G.THIS_YEAR_SALES, G.LAST_YEAR_SALES, G.GROWTH_RATE FROM SALES_GROWTH G WHERE THIS_YEAR >= TO_CHAR(SYSDATE, 'YYYY')||'01' UNION SELECT TO_CHAR(ADD_MONTHS(TRUNC(SYSDATE, 'YEAR'), LEVEL - 1), 'YYYYMM') AS MONTH ,null,null,null,null FROM DUAL CONNECT BY LEVEL <= 12 ) GROUP BY THIS_YEAR ORDER BY THIS_YEAR

/* rstMgn.getSellingBranchList 지사별 순매출 */ WITH MONTHS_12 AS () ,EACH_PAYMENT AS (
SELECT M.month, DATA.BRANCH_ID, DATA.TOTAL_INCOME FROM MONTHS_12 M ,( SELECT RESULT_MONTH ,BRANCH_ID ,SUM( PAYMENT) TOTAL_INCOME FROM( SELECT DISTINCT TO_CHAR( R.RESULT_DATE, 'YYYYMM') AS RESULT_MONTH ,R.INCOME ,P.PAYMENT ,DIV.DEPTH2 AS BRANCH_ID FROM TBL_EXP_RESULT R ,TBL_EXP_CONTRACT C ,TBL_EXP_PAYMENT P ,TBL_EXP_CONTRACT_MEMBER CM ,TBL_EXP_USER U ,TBL_EXP_DIVISION DIV WHERE 1=1 AND U.DIVISION_ID = DIV.DIVISION_ID AND U.DIVISION_ID LIKE 'SALES_DIV%' /* 일때, 지사가 있음 */ AND C.CONTRACT_ID = R.CONTRACT_ID AND R.CONTRACT_ID = P.CONTRACT_ID AND R.RESULT_ID = P.RESULT_ID AND C.CONTRACT_ID = CM.CONTRACT_ID AND P.USER_ID = CM.USER_ID AND P.USER_ID = U.USER_ID AND R.USE_YN = 'Y' AND R.DEL_YN = 'N' AND P.USE_YN = 'Y' AND P.DEL_YN = 'N' AND CM.USE_YN = 'Y' AND CM.DEL_YN = 'N' AND TO_CHAR(R.RESULT_DATE, 'YYYY') >= TO_CHAR(ADD_MONTHS(TRUNC(SYSDATE), -12), 'YYYY') AND TO_CHAR(R.RESULT_DATE, 'YYYY') <= TO_CHAR(ADD_MONTHS(TRUNC(SYSDATE), 0), 'YYYY') AND C.CONTRACT_ID = CM.CONTRACT_ID
) GROUP BY RESULT_MONTH ,BRANCH_ID ) DATA WHERE M.MONTH = DATA.RESULT_MONTH(+) )
SELECT DISTINCT BRANCH_ID FROM EACH_PAYMENT WHERE BRANCH_ID IS NOT NULL ORDER BY BRANCH_ID
SELECT MONTH, ROUND( SUM(TOTAL_INCOME) / 100000000 , 2 ) AS TOTAL_INCOME FROM ( SELECT MONTH, BRANCH_ID ,TOTAL_INCOME FROM EACH_PAYMENT WHERE ( BRANCH_ID = #{BRANCH_ID} OR BRANCH_ID IS NULL ) ORDER BY MONTH ) GROUP BY MONTH ORDER BY MONTH

/* rstMgn.getFASellingOrder 전체 FA 매출 순위 */ WITH EACH_PAYMENT AS ( SELECT DATA.RESULT_YEAR , DATA.USER_ID , DATA.USER_NM , DATA.TOTAL_INCOME FROM ( SELECT RESULT_YEAR , USER_ID , USER_NM , SUM(PAYMENT) TOTAL_INCOME FROM ( SELECT DISTINCT TO_CHAR( R.RESULT_DATE, 'YYYY') AS RESULT_YEAR , R.INCOME , P.PAYMENT , U.BRANCH_ID , U.USER_ID , U.USER_NM FROM TBL_EXP_RESULT R , TBL_EXP_CONTRACT C , TBL_EXP_PAYMENT P , TBL_EXP_CONTRACT_MEMBER CM , TBL_EXP_USER U WHERE C.CONTRACT_ID = R.CONTRACT_ID AND R.CONTRACT_ID = P.CONTRACT_ID AND R.RESULT_ID = P.RESULT_ID AND C.CONTRACT_ID = CM.CONTRACT_ID AND P.RELATION_TYPE = CM.RELATION_TYPE AND P.USER_ID = CM.USER_ID AND P.USER_ID = U.USER_ID AND R.USE_YN = 'Y' AND R.DEL_YN = 'N' AND P.USE_YN = 'Y' AND P.DEL_YN = 'N' AND CM.USE_YN = 'Y' AND CM.DEL_YN = 'N'
AND TO_CHAR(R.RESULT_DATE, 'YYYY') >= TO_CHAR( SYSDATE , 'YYYY')
AND TO_CHAR(R.RESULT_DATE, 'YYYY') >= TO_CHAR( SYSDATE - INTERVAL '1' YEAR ,'YYYY') AND TO_CHAR(R.RESULT_DATE, 'YYYY') < TO_CHAR( SYSDATE , 'YYYY')
) GROUP BY RESULT_YEAR , USER_ID , USER_NM ) DATA ) SELECT RESULT_YEAR AS YEAR , USER_NM , ROUND(NVL(SUM(TOTAL_INCOME),0) / 100000000,2) AS TOTAL_INCOME FROM EACH_PAYMENT WHERE USER_NM IS NOT NULL GROUP BY RESULT_YEAR, USER_NM ORDER BY TOTAL_INCOME DESC FETCH NEXT 12 ROWS ONLY
