mapper.insert:
/* Sample.setFile */ INSERT INTO TBL_EXP_FILEMNG( UUID ,GRP_UUID ,ORG_FILE_NAME ,STORED_FILE_NAME ,FILE_PATH
,FILE_SIZE ,FILE_TYPE ,ORDER_NUMBER ,CREATE_USER ,CREATE_DATE ) VALUES( #{uuid}, #{grpUuid}, #{orgFileName}, #{storedFileName}, #{filePath},
TO_NUMBER(#{fileSize}), #{fileType}, (SELECT NVL2( MAX(ORDER_NUMBER), MAX(ORDER_NUMBER)+1 ,1 ) AS ORDER_NUMBER FROM TBL_EXP_FILEMNG WHERE GRP_UUID = #{grpUuid}), #{createUser}, (TO_DATE(#{createDate} , 'yyyy/mm/dd hh24:mi:ss') ) )

mapper.select:
/* Sample.getFile */ SELECT GRP_FILE_ID ,FILE_ID ,ORG_FILE_NM ,STORED_FILE_NM ,FILE_SIZE ,FILE_TYPE ,ORDER_NUM ,DEL_YN ,OWNER_CD ,UPDATE_USER ,UPDATE_DATE ,CREATE_USER ,CREATE_DATE FROM TBL_EXP_FILEMNG WHERE 1 = 1
AND GRP_FILE_ID = #{grpFileId}
AND FILE_ID = #{fileId}
ORDER BY ORDER_NUM , CREATE_DATE DESC

mapper.delete:
/* Sample.deleteFile */
DELETE FROM TBL_EXP_FILEMNG
WHERE  1 = 1

  AND    GRP_ID = #{grpId}


  AND    UUID = #{uuid}

mapper.select:
/* Sample.getBoardList */ SELECT UUID ,BOARD_CODE ,TITLE ,CONTENTS ,DELETE_YN ,UPDATE_USER ,UPDATE_DATE ,CREATE_USER ,CREATE_DATE FROM TBL_EXP_BOARD WHERE 1 = 1
AND BOARD_CODE = #{boardCode}
AND UUID = #{uuid}
ORDER BY CREATE_DATE DESC

