/* schedule.getScheduleList */ SELECT A.SP_CSTM_ID ,A.SCHEDULE_ID ,A.SCHEDULE_GRP_ID ,A.LOOP_TYPE ,A.LOOP_TYPE_DTL_CD ,A.LOOP_TYPE_DTL_VAL ,A.SCHEDULE_LOOP_END_DATE ,A.TITLE ,A.WORK_TYPE ,A.ALL_APPLY_YN ,A.SCHEDULE_START_DATE ,A.SCHEDULE_END_DATE ,A.TIME_TYPE ,A.START_TIME_HOUR ,A.START_TIME_MINUTE ,A.END_TIME_HOUR ,A.END_TIME_MINUTE ,A.CONTRACT_ID ,A.POSITION ,A.SECRET_YN ,A.DESCRIPTION ,A.USER_ID ,CASE WHEN A.BUYER_NM != NULL THEN A.BUYER_NM ||'/'||A.CONSULTING_NM ELSE NULL END AS CONTRACT_NM ,A.UPDATE_USER ,A.UPDATE_DATE ,A.CREATE_USER ,A.CREATE_DATE FROM ( SELECT A.SP_CSTM_ID ,A.SCHEDULE_ID ,A.SCHEDULE_GRP_ID ,A.LOOP_TYPE ,A.LOOP_TYPE_DTL_CD ,A.LOOP_TYPE_DTL_VAL ,TO_CHAR(A.SCHEDULE_LOOP_END_DATE,'YYYY-MM-DD') AS SCHEDULE_LOOP_END_DATE ,A.TITLE ,A.WORK_TYPE ,A.ALL_APPLY_YN ,TO_CHAR(A.SCHEDULE_START_DATE,'YYYY-MM-DD') AS SCHEDULE_START_DATE ,TO_CHAR(A.SCHEDULE_END_DATE,'YYYY-MM-DD') AS SCHEDULE_END_DATE ,A.TIME_TYPE ,A.START_TIME_HOUR ,A.START_TIME_MINUTE ,A.END_TIME_HOUR ,A.END_TIME_MINUTE ,A.CONTRACT_ID ,A.POSITION ,A.SECRET_YN ,A.DESCRIPTION ,A.USER_ID ,(SELECT BUYER_NM FROM TBL_EXP_BUYER WHERE BUYER_ID = B.BUYER_ID) AS BUYER_NM ,(SELECT CONSULTING_NM FROM TBL_EXP_CONSULTING WHERE CONSULTING_ID = B.CONSULTING_ID) AS CONSULTING_NM ,A.UPDATE_USER ,A.UPDATE_DATE ,A.CREATE_USER ,A.CREATE_DATE FROM TBL_EXP_SCHEDULE A LEFT OUTER JOIN TBL_EXP_CONTRACT B ON 1 = 1 AND A.CONTRACT_ID = B.CONTRACT_ID WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId}
AND A.SCHEDULE_ID = #{scheduleId}
AND TRIM(A.TITLE) LIKE '%' || TRIM(#{title})||'%'
) A WHERE 1 = 1

/* schedule.getScheduleDtl */ SELECT A.SCHEDULE_ID ,A.SCHEDULE_GRP_ID ,A.TITLE ,A.WORK_TYPE ,GET_CODE_NM('SCHEDULE_WORK_TYPE', A.WORK_TYPE) AS WORK_TYPE_NM ,A.ALL_APPLY_YN ,A.SCHEDULE_START_DATE ,TO_CHAR(A.SCHEDULE_START_DATE ,'YYYY-MM-DD') AS SCHEDULE_START_YMD ,A.TIME_TYPE ,A.START_TIME_HOUR ,GET_CODE_NM('TIME_HOUR_24', A.START_TIME_HOUR) AS START_TIME_HOUR_NM ,A.START_TIME_MINUTE ,GET_CODE_NM('TIME_MINUTE_30', A.START_TIME_MINUTE) AS START_TIME_MINUTE_NM ,TO_CHAR(A.SCHEDULE_END_DATE ,'YYYY-MM-DD') AS SCHEDULE_END_YMD ,A.END_TIME_HOUR ,GET_CODE_NM('TIME_HOUR_24', A.END_TIME_HOUR) AS END_TIME_HOUR_NM ,A.END_TIME_MINUTE ,GET_CODE_NM('TIME_MINUTE_30', A.END_TIME_MINUTE) AS END_TIME_MINUTE_NM ,GET_CODE_NM('SCHEDULE_LOOP_TYPE', A.LOOP_TYPE) AS LOOP_TYPE_NM ,B.TAX_MNG AS CONTRACT ,A.POSITION ,A.SECRET_YN ,GET_CODE_NM('SCHEDULE_SECRET_YN', A.SECRET_YN) AS SECRET_YN_NM ,A.DESCRIPTION ,A.USER_ID ,GET_USER_NM(A.USER_ID) AS USER_NM ,A.SP_CSTM_ID FROM TBL_EXP_SCHEDULE A LEFT OUTER JOIN TBL_EXP_CONTRACT B ON 1 = 1 AND A.CONTRACT_ID = B.CONTRACT_ID WHERE 1 = 1
AND A.SCHEDULE_GRP_ID = #{scheduleGrpId}
AND A.SCHEDULE_ID = #{scheduleId}
AND TO_CHAR(A.SCHEDULE_START_DATE ,'YYYY-MM-DD') = #{scheduleDate}
AND A.SP_CSTM_ID = #{spCstmId}
AND A.USER_ID = #{userId.userId}

/* schedule.getTeamNotRepeat */ WITH TABLE_DATE AS ( SELECT TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'YYYY-MM-DD') DTE ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'DAY') DAY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'DY') DY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'D') D FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{plusDate},'YYYY-MM-DD') - TO_DATE(#{minusDate},'YYYY-MM-DD') ) SELECT A.SCHEDULE_ID ,D.DTE AS SCHEDULE_DATE ,TO_CHAR(A.SCHEDULE_START_DATE,'YYYY-MM-DD') AS SCHEDULE_START_DATE ,TO_CHAR(A.SCHEDULE_END_DATE,'YYYY-MM-DD') AS SCHEDULE_END_DATE ,LPAD(A.START_TIME_HOUR , 2, '0') AS START_TIME_HOUR ,A.START_TIME_MINUTE ,LPAD(A.END_TIME_HOUR , 2, '0') AS END_TIME_HOUR ,A.END_TIME_MINUTE ,TITLE ,'['||GET_USER_NM(A.USER_ID)||']'||A.TITLE AS TITLE1 ,A.CONTRACT_ID ,C.BUYER_ID ,C.BUYER_NM ,A.USER_ID ,GET_USER_NM(A.USER_ID) AS USER_NM ,A.POSITION ,A.WORK_TYPE ,GET_CODE_NM('SCHEDULE_WORK_TYPE', A.WORK_TYPE) AS WORK_TYPE_NM ,'N' AS SHARE_YN ,B.ATTR1 AS WORK_COLOR ,B.ATTR2 AS TEXT_COLOR FROM TBL_EXP_SCHEDULE A JOIN ( SELECT A.GRP_CODE_ID ,B.CODE_ID ,B.ATTR1 ,B.ATTR2 FROM TBL_EXP_GRPCODE A JOIN TBL_EXP_CODE B ON 1 = 1 AND B.SP_CSTM_ID = #{sessionVo.spCstmId} AND B.DEL_YN = 'N' AND A.GRP_CODE_ID = B.GRP_CODE_ID WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId} AND A.DEL_YN = 'N' AND A.GRP_CODE_ID = 'SCHEDULE_WORK_TYPE' ) B ON 1 = 1 AND A.WORK_TYPE = B.CODE_ID LEFT OUTER JOIN ( SELECT A.CONTRACT_ID ,A.BUYER_ID ,B.BUYER_NM FROM TBL_EXP_CONTRACT A LEFT OUTER JOIN TBL_EXP_BUYER B ON 1 = 1 AND A.BUYER_ID = B.BUYER_ID AND B.SP_CSTM_ID = #{sessionVo.spCstmId} WHERE 1 = 1 ) C ON 1 = 1 AND A.CONTRACT_ID = C.CONTRACT_ID JOIN TABLE_DATE D ON 1 = 1 AND A.SCHEDULE_START_DATE = D.DTE WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId} AND A.LOOP_TYPE = 'NOT_REPEAT' /*반복안함*/ AND D.DTE <= A.SCHEDULE_LOOP_END_DATE AND A.USER_ID IN
#{userId}
AND ( ( #{startDate} <= A.SCHEDULE_START_DATE AND A.SCHEDULE_START_DATE <= #{endDate} ) OR ( A.SCHEDULE_START_DATE <= #{startDate} AND #{endDate} <= A.SCHEDULE_END_DATE) OR ( #{startDate} <= A.SCHEDULE_END_DATE AND A.SCHEDULE_END_DATE <= #{endDate} ) )
AND A.CONTRACT_ID = #{contractId}
AND A.TITLE LIKE '%'||#{searchData}||'%'

