AND LOOP_TYPE = 'DAY' ORDER BY CREATE_DATE DESC ) ,TABLE_DATE AS ( SELECT DTE ,DY ,D FROM ( SELECT TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD')-1 + LEVEL , 'YYYY-MM-DD') DTE ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD')-1 + LEVEL , 'DAY') DAY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD')-1 + LEVEL , 'DY') DY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD')-1 + LEVEL , 'D') D FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{plusDate},'YYYY-MM-DD') - TO_DATE(#{minusDate},'YYYY-MM-DD') ) ORDER BY DTE ) SELECT A.SCHEDULE_GRP_ID ,A.SCHEDULE_ID ,B.DTE AS SCHEDULE_DATE ,B.DTE AS SCHEDULE_START_DATE ,A.START_TIME_HOUR ,A.START_TIME_MINUTE ,B.DTE AS SCHEDULE_END_DATE ,A.END_TIME_HOUR ,A.END_TIME_MINUTE ,A.TITLE ,'['||GET_USER_NM(A.USER_ID)||']'||A.TITLE AS TITLE1 ,A.CONTRACT_ID ,D.BUYER_ID ,D.BUYER_NM ,A.USER_ID ,GET_USER_NM(A.USER_ID) AS USER_NM ,'N' AS SHARE_YN ,A.POSITION ,A.WORK_TYPE ,GET_CODE_NM('SCHEDULE_WORK_TYPE', A.WORK_TYPE) AS WORK_TYPE_NM ,C.ATTR1 AS WORK_COLOR ,C.ATTR2 AS TEXT_COLOR FROM TABLE_SCHEDULE A JOIN TABLE_DATE B ON 1 = 1 AND A.SCHEDULE_START_DATE_STR <= B.DTE JOIN ( SELECT A.GRP_CODE_ID ,B.CODE_ID ,B.ATTR1 ,B.ATTR2 FROM TBL_EXP_GRPCODE A JOIN TBL_EXP_CODE B ON 1 = 1 AND B.SP_CSTM_ID = #{sessionVo.spCstmId} AND B.DEL_YN = 'N' AND A.GRP_CODE_ID = B.GRP_CODE_ID WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId} AND A.DEL_YN = 'N' AND A.GRP_CODE_ID = 'SCHEDULE_WORK_TYPE' ) C ON 1 = 1 AND A.WORK_TYPE = C.CODE_ID LEFT OUTER JOIN ( SELECT A.CONTRACT_ID ,A.BUYER_ID ,B.BUYER_NM FROM TBL_EXP_CONTRACT A LEFT OUTER JOIN TBL_EXP_BUYER B ON 1 = 1 AND A.BUYER_ID = B.BUYER_ID AND B.SP_CSTM_ID = #{sessionVo.spCstmId} WHERE 1 = 1 ) D ON 1 = 1 AND A.CONTRACT_ID = D.CONTRACT_ID WHERE 1 = 1 AND B.DTE <= A.SCHEDULE_LOOP_END_DATE
