/* schedule.getTeamWeekRepeat */ WITH BASE_TABEL AS( SELECT DTE ,DY ,D FROM ( SELECT TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'YYYY-MM-DD') DTE ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'DAY') DAY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'DY') DY ,TO_CHAR( TO_DATE(#{minusDate},'YYYY-MM-DD') + LEVEL , 'D') D FROM DUAL CONNECT BY LEVEL <= TO_DATE(#{plusDate},'YYYY-MM-DD') - TO_DATE(#{minusDate},'YYYY-MM-DD') ) ORDER BY DTE ), TABLE_SCHEDULE AS ( SELECT SCHEDULE_ID ,TO_CHAR(SCHEDULE_START_DATE,'YYYY-MM-DD') AS SCHEDULE_START_DATE ,TO_CHAR(SCHEDULE_START_DATE, 'd') AS WEEK_START ,TO_CHAR(SCHEDULE_END_DATE,'YYYY-MM-DD') AS SCHEDULE_END_DATE ,TO_CHAR(SCHEDULE_END_DATE, 'd') AS WEEK_END FROM TBL_EXP_SCHEDULE WHERE 1 = 1 AND SP_CSTM_ID = #{sessionVo.spCstmId} AND USER_ID IN
#{userId}
AND LOOP_TYPE = 'WEEK' ) SELECT A.SCHEDULE_ID ,A.SCHEDULE_DATE ,A.SCHEDULE_START_DATE ,A.START_TIME_HOUR ,A.START_TIME_MINUTE ,A.SCHEDULE_END_DATE ,A.END_TIME_HOUR ,A.END_TIME_MINUTE ,A.TITLE ,A.TITLE1 ,A.CONTRACT_ID ,A.BUYER_ID ,A.BUYER_NM ,A.USER_ID ,GET_USER_NM(A.USER_ID) AS USER_NM ,A.POSITION ,A.WORK_TYPE ,A.WORK_TYPE_NM ,A.SHARE_YN ,A.WORK_COLOR ,A.TEXT_COLOR FROM ( /*=====================================================================*/ SELECT A.GROUP_ID , SUBSTR(A.GROUP_ID, 1, INSTR(A.GROUP_ID, '#') - 1) AS SCHEDULE_ID , B.SCHEDULE_LOOP_END_DATE , SUBSTR(A.GROUP_ID, INSTR(A.GROUP_ID, '#', 1, 1) + 1, INSTR(A.GROUP_ID, '#', 1, 2) - INSTR(A.GROUP_ID, '#', 1, 1) - 1) AS SCHEDULE_DATE , TO_CHAR( TO_DATE(SUBSTR(A.GROUP_ID, INSTR(A.GROUP_ID, '#', 1, 1) + 1, INSTR(A.GROUP_ID, '#', 1, 2) - INSTR(A.GROUP_ID, '#', 1, 1) - 1), 'YYYY-MM-DD HH24:MI:SS') + (TO_NUMBER( SUBSTR(A.GROUP_ID, INSTR(A.GROUP_ID, '#', 1, 3) + 1)) * 7)-7, 'YYYY-MM-DD' ) AS SCHEDULE_START_DATE , B.START_TIME_HOUR , B.START_TIME_MINUTE , TO_CHAR( TO_DATE( SUBSTR(A.GROUP_ID, INSTR(A.GROUP_ID, '#', 1, 2) + 1, INSTR(A.GROUP_ID, '#', 1, 3) - INSTR(A.GROUP_ID, '#', 1, 2) - 1) , 'YYYY-MM-DD HH24:MI:SS') + (TO_NUMBER( SUBSTR(A.GROUP_ID, INSTR(A.GROUP_ID, '#', 1, 3) + 1)) * 7)-7, 'YYYY-MM-DD' ) AS SCHEDULE_END_DATE , B.END_TIME_HOUR , B.END_TIME_MINUTE , B.TITLE , '['||GET_USER_NM(B.USER_ID)||']'||B.TITLE AS TITLE1 , B.CONTRACT_ID , B.USER_ID , D.BUYER_ID , D.BUYER_NM , B.POSITION , B.WORK_TYPE , GET_CODE_NM('SCHEDULE_WORK_TYPE', B.WORK_TYPE) AS WORK_TYPE_NM , A.SHARE_YN AS SHARE_YN , C.ATTR1 AS WORK_COLOR , C.ATTR2 AS TEXT_COLOR FROM ( SELECT A.GROUP_ID ,'N' AS SHARE_YN ,MAX(A.DY_START) AS DY_START ,MAX(A.D_START) AS D_START ,MAX(A.DY_END) AS DY_END ,MAX(A.D_END) AS D_END FROM ( /*======= 사용자 일정 사용자 일정 사용자 일정 =======*/ SELECT A.GROUP_ID||'#'||A.GRP_CNT AS GROUP_ID ,A.DY_START ,A.D_START ,A.DY_END ,A.D_END FROM (
SELECT A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE AS GROUP_ID ,ROW_NUMBER() OVER(PARTITION BY A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE ORDER BY A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE) AS GRP_CNT ,BASE_TABEL.DY AS DY_START ,BASE_TABEL.D AS D_START ,NULL AS DY_END ,NULL AS D_END FROM BASE_TABEL ,TABLE_SCHEDULE A WHERE 1 = 1 AND BASE_TABEL.D = A.WEEK_START ) A UNION ALL SELECT A.GROUP_ID||'#'||A.GRP_CNT AS GROUP_ID ,A.DY_START ,A.D_START ,A.DY_END ,A.D_END FROM (
SELECT A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE AS GROUP_ID ,ROW_NUMBER() OVER(PARTITION BY A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE ORDER BY A.SCHEDULE_ID||'#'||A.SCHEDULE_START_DATE||'#'||A.SCHEDULE_END_DATE) AS GRP_CNT ,NULL AS DY_START ,NULL AS D_START ,BASE_TABEL.DY AS DY_END ,BASE_TABEL.D AS D_END FROM BASE_TABEL ,TABLE_SCHEDULE A WHERE 1 = 1 AND BASE_TABEL.D = A.WEEK_END ) A /*####### 사용자 일정 사용자 일정 사용자 일정 #######*/ ) A GROUP BY A.GROUP_ID ) A JOIN TBL_EXP_SCHEDULE B ON 1 = 1 AND B.SP_CSTM_ID = #{sessionVo.spCstmId} AND B.LOOP_TYPE = 'WEEK' AND B.USER_ID IN
#{userId}
AND SUBSTR(A.GROUP_ID, 1, INSTR(A.GROUP_ID, '#') - 1) = B.SCHEDULE_ID JOIN ( SELECT A.GRP_CODE_ID ,B.CODE_ID ,B.ATTR1 ,B.ATTR2 FROM TBL_EXP_GRPCODE A JOIN TBL_EXP_CODE B ON 1 = 1 AND B.SP_CSTM_ID = #{sessionVo.spCstmId} AND B.DEL_YN = 'N' AND A.GRP_CODE_ID = B.GRP_CODE_ID WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId} AND A.DEL_YN = 'N' AND A.GRP_CODE_ID = 'SCHEDULE_WORK_TYPE' ) C ON 1 = 1 AND B.WORK_TYPE = C.CODE_ID LEFT OUTER JOIN ( SELECT A.CONTRACT_ID ,A.BUYER_ID ,B.BUYER_NM FROM TBL_EXP_CONTRACT A LEFT OUTER JOIN TBL_EXP_BUYER B ON 1 = 1 AND A.BUYER_ID = B.BUYER_ID AND B.SP_CSTM_ID = #{sessionVo.spCstmId} WHERE 1 = 1 ) D ON 1 = 1 AND B.CONTRACT_ID = D.CONTRACT_ID WHERE 1 = 1 /*=====================================================================*/ ) A WHERE 1 = 1 AND A.SCHEDULE_START_DATE <= A.SCHEDULE_LOOP_END_DATE
